mapper.select:
SELECT * FROM MENTOR


	 MENTORING_LOCATION = #{mentoring_location}


	AND MENTOR_DUTY = #{mentor_duty}

SELECT * FROM(
	SELECT ROWNUM RN, A.* FROM(
		SELECT * FROM MENTOR
		ORDER BY MENTOR_ID
		) A
	)
WHERE RN BETWEEN #{start} AND #{end}

	SELECT MENTOR_ID
 	FROM MENTOR
 	WHERE ID=#{id}
